{"file_text":"type KeyCallback = (key: string) => void;

class InputManager {
  private keys: Set<string> = new Set();
  private keyDownCallbacks: KeyCallback[] = [];
  private keyUpCallbacks: KeyCallback[] = [];
  private shiftPressed: boolean = false;

  constructor() {
    this.handleKeyDown = this.handleKeyDown.bind(this);
    this.handleKeyUp = this.handleKeyUp.bind(this);
  }

  init(): void {
    window.addEventListener('keydown', this.handleKeyDown);
    window.addEventListener('keyup', this.handleKeyUp);
  }

  destroy(): void {
    window.removeEventListener('keydown', this.handleKeyDown);
    window.removeEventListener('keyup', this.handleKeyUp);
  }

  private handleKeyDown(e: KeyboardEvent): void {
    // Prevent default for game keys
    if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', ' '].includes(e.key)) {
      e.preventDefault();
    }

    this.shiftPressed = e.shiftKey;

    if (!this.keys.has(e.key)) {
      this.keys.add(e.key);
      this.keyDownCallbacks.forEach(cb => cb(e.key));
    }
  }

  private handleKeyUp(e: KeyboardEvent): void {
    this.shiftPressed = e.shiftKey;
    this.keys.delete(e.key);
    this.keyUpCallbacks.forEach(cb => cb(e.key));
  }

  isKeyPressed(key: string): boolean {
    return this.keys.has(key);
  }

  isShiftPressed(): boolean {
    return this.shiftPressed;
  }

  onKeyDown(callback: KeyCallback): void {
    this.keyDownCallbacks.push(callback);
  }

  onKeyUp(callback: KeyCallback): void {
    this.keyUpCallbacks.push(callback);
  }

  clearCallbacks(): void {
    this.keyDownCallbacks = [];
    this.keyUpCallbacks = [];
  }
}

export const inputManager = new InputManager();
