export class Renderer {
  private canvas: HTMLCanvasElement;
  private ctx: CanvasRenderingContext2D;

  constructor(canvas: HTMLCanvasElement) {
    this.canvas = canvas;
    this.canvas.width = GAME_WIDTH;
    this.canvas.height = GAME_HEIGHT;

    const ctx = canvas.getContext('2d');
    if (!ctx) {
      throw new Error('Could not get 2D context');
    }
    this.ctx = ctx;
  }

  get context(): CanvasRenderingContext2D {
    return this.ctx;
  }

  clear(): void {
    this.ctx.clearRect(0, 0, GAME_WIDTH, GAME_HEIGHT);
  }

  drawSky(): void {
    const gradient = this.ctx.createLinearGradient(0, 0, 0, GAME_HEIGHT);
    gradient.addColorStop(0, '#87CEEB');
    gradient.addColorStop(1, '#E0F6FF');
    this.ctx.fillStyle = gradient;
    this.ctx.fillRect(0, 0, GAME_WIDTH, GAME_HEIGHT);
  }

  drawTerrain(terrainPoints: { x: number; y: number }[]): void {
    if (terrainPoints.length < 2) return;

    this.ctx.beginPath();
    this.ctx.moveTo(terrainPoints[0].x, terrainPoints[0].y);

    for (let i = 1; i < terrainPoints.length; i++) {
      this.ctx.lineTo(terrainPoints[i].x, terrainPoints[i].y);
    }

    this.ctx.lineTo(terrainPoints[terrainPoints.length - 1].x, GAME_HEIGHT);
    this.ctx.lineTo(terrainPoints[0].x, GAME_HEIGHT);
    this.ctx.closePath();

    const gradient = this.ctx.createLinearGradient(0, 400, 0, GAME_HEIGHT);
    gradient.addColorStop(0, '#228B22');
    gradient.addColorStop(0.5, '#006400');
    gradient.addColorStop(1, '#004400');
    this.ctx.fillStyle = gradient;
    this.ctx.fill();

    // Draw grass line
    this.ctx.strokeStyle = '#32CD32';
    this.ctx.lineWidth = 3;
    this.ctx.beginPath();
    this.ctx.moveTo(terrainPoints[0].x, terrainPoints[0].y);
    for (let i = 1; i < terrainPoints.length; i++) {
      this.ctx.lineTo(terrainPoints[i].x, terrainPoints[i].y);
    }
    this.ctx.stroke();
  }

  drawHelipad(x: number, y: number, width: number, height: number): void {
    // Pad base
    this.ctx.fillStyle = '#333';
    this.ctx.fillRect(x, y, width, height);

    // H marking
    this.ctx.fillStyle = '#FFF';
    this.ctx.font = 'bold 12px Arial';
    this.ctx.textAlign = 'center';
    this.ctx.fillText('H', x + width / 2, y + height - 1);

    // Edge lines
    this.ctx.strokeStyle = '#FFD700';
    this.ctx.lineWidth = 2;
    this.ctx.strokeRect(x, y, width, height);
  }

  drawHelicopter(x: number, y: number, width: number, height: number, gunAngle: number): void {
    this.ctx.save();
    this.ctx.translate(x + width / 2, y + height / 2);

    // Body
    this.ctx.fillStyle = '#2E8B57';
    this.ctx.beginPath();
    this.ctx.ellipse(0, 0, width / 2, height / 2.5, 0, 0, Math.PI * 2);
    this.ctx.fill();
    this.ctx.strokeStyle = '#1D5A3A';
    this.ctx.lineWidth = 2;
    this.ctx.stroke();

    // Cockpit
    this.ctx.fillStyle = '#87CEEB';
    this.ctx.beginPath();
    this.ctx.ellipse(width / 4, -2, width / 5, height / 4, 0, 0, Math.PI * 2);
    this.ctx.fill();
    this.ctx.strokeStyle = '#333';
    this.ctx.lineWidth = 1;
    this.ctx.stroke();

    // Main rotor
    this.ctx.strokeStyle = '#333';
    this.ctx.lineWidth = 3;
    this.ctx.beginPath();
    this.ctx.moveTo(-width / 2, -height / 2 - 5);
    this.ctx.lineTo(width / 2, -height / 2 - 5);
    this.ctx.stroke();

    // Rotor hub
    this.ctx.fillStyle = '#666';
    this.ctx.beginPath();
    this.ctx.arc(0, -height / 2 - 5, 5, 0, Math.PI * 2);
    this.ctx.fill();

    // Tail
