    this.ctx.beginPath();
    this.ctx.moveTo(-width / 2 + 5, 0);
    this.ctx.lineTo(-width / 2 - 25, -5);
    this.ctx.lineTo(-width / 2 - 25, 5);
    this.ctx.closePath();
    this.ctx.fill();
    this.ctx.stroke();

    // Tail rotor
    this.ctx.strokeStyle = '#333';
    this.ctx.lineWidth = 2;
    this.ctx.beginPath();
    this.ctx.moveTo(-width / 2 - 25, -12);
    this.ctx.lineTo(-width / 2 - 25, 12);
    this.ctx.stroke();

    // Skids
    this.ctx.strokeStyle = '#333';
    this.ctx.lineWidth = 3;
    this.ctx.beginPath();
    this.ctx.moveTo(-width / 4, height / 2 + 5);
    this.ctx.lineTo(width / 4, height / 2 + 5);
    this.ctx.stroke();

    // Skid supports
    this.ctx.lineWidth = 2;
    this.ctx.beginPath();
    this.ctx.moveTo(-width / 6, height / 3);
    this.ctx.lineTo(-width / 6, height / 2 + 5);
    this.ctx.moveTo(width / 6, height / 3);
    this.ctx.lineTo(width / 6, height / 2 + 5);
    this.ctx.stroke();

    // Gun (rotates with gunAngle)
    this.ctx.save();
    this.ctx.translate(width / 3, height / 4);
    this.ctx.rotate((gunAngle * Math.PI) / 180);

    this.ctx.fillStyle = '#444';
    this.ctx.fillRect(0, -3, 20, 6);
    this.ctx.fillStyle = '#222';
    this.ctx.beginPath();
    this.ctx.arc(0, 0, 5, 0, Math.PI * 2);
    this.ctx.fill();

    this.ctx.restore();
    this.ctx.restore();
  }

  drawBullet(x: number, y: number, radius: number): void {
    this.ctx.fillStyle = '#FFD700';
    this.ctx.beginPath();
    this.ctx.arc(x, y, radius, 0, Math.PI * 2);
    this.ctx.fill();

