    this.ctx.beginPath();
    this.ctx.arc(x + width / 2, y + height / 2.5, width / 8, 0, Math.PI * 2);
    this.ctx.fill();

    // String
    this.ctx.strokeStyle = '#666';
    this.ctx.lineWidth = 1;
    this.ctx.beginPath();
    this.ctx.moveTo(x + width / 2, y + height * 0.8);
    this.ctx.lineTo(x + width / 2, y + height);
    this.ctx.stroke();
