    gradient.addColorStop(0, '#FFFF00');
    gradient.addColorStop(0.5, '#FF8800');
    gradient.addColorStop(1, 'transparent');

    this.ctx.fillStyle = gradient;
    this.ctx.beginPath();
    this.ctx.arc(x, y, radius, 0, Math.PI * 2);
    this.ctx.fill();

    this.ctx.restore();
  }
}
