{"file_text":"import { FRAME_TIME } from '../game/constants';

export type UpdateCallback = (dt: number) => void;
export type RenderCallback = () => void;

export class GameLoop {
  private running: boolean = false;
  private lastTime: number = 0;
  private accumulator: number = 0;
  private updateCallback: UpdateCallback;
  private renderCallback: RenderCallback;
  private animationFrameId: number | null = null;

  constructor(updateCallback: UpdateCallback, renderCallback: RenderCallback) {
    this.updateCallback = updateCallback;
    this.renderCallback = renderCallback;
    this.tick = this.tick.bind(this);
  }

  start(): void {
    if (this.running) return;
    this.running = true;
    this.lastTime = performance.now();
    this.accumulator = 0;
    this.animationFrameId = requestAnimationFrame(this.tick);
  }

  stop(): void {
    this.running = false;
    if (this.animationFrameId !== null) {
      cancelAnimationFrame(this.animationFrameId);
      this.animationFrameId = null;
    }
  }

  private tick(currentTime: number): void {
    if (!this.running) return;

    const deltaTime = Math.min(currentTime - this.lastTime, 100); // Cap to prevent spiral
    this.lastTime = currentTime;
    this.accumulator += deltaTime;

    // Fixed timestep updates
    while (this.accumulator >= FRAME_TIME) {
      this.updateCallback(FRAME_TIME);
      this.accumulator -= FRAME_TIME;
    }

    this.renderCallback();
    this.animationFrameId = requestAnimationFrame(this.tick);
  }

  isRunning(): boolean {
    return this.running;
  }
}
