  GUN_MAX_ANGLE,
  GUN_ANGLE_STEP,
  GAME_HEIGHT,
} from '../constants';

export class Helicopter implements Entity {
  x: number = HELICOPTER_START_X;
  y: number = HELICOPTER_START_Y;
  width: number = HELICOPTER_WIDTH;
  height: number = HELICOPTER_HEIGHT;
  active: boolean = true;

  velocityX: number = 0;
  velocityY: number = 0;
  gunAngle: number = 0; // degrees, 0 = horizontal, negative = up

  private movingUp: boolean = false;
  private movingDown: boolean = false;
  private accelerating: boolean = false;
  private decelerating: boolean = false;

  reset(): void {
    this.x = HELICOPTER_START_X;
    this.y = HELICOPTER_START_Y;
    this.velocityX = 0;
    this.velocityY = 0;
    this.gunAngle = 0;
  }

  setMovingUp(value: boolean): void {
    this.movingUp = value;
  }

  setMovingDown(value: boolean): void {
    this.movingDown = value;
  }

  setAccelerating(value: boolean): void {
    this.accelerating = value;
  }

  setDecelerating(value: boolean): void {
    this.decelerating = value;
  }

  aimUp(): void {
    this.gunAngle = Math.max(this.gunAngle - GUN_ANGLE_STEP, GUN_MIN_ANGLE);
  }

  aimDown(): void {
    this.gunAngle = Math.min(this.gunAngle + GUN_ANGLE_STEP, GUN_MAX_ANGLE);
  }

  getGunPosition(): { x: number; y: number } {
    // Gun is at front-bottom of helicopter
    return {
      x: this.x + this.width * 0.83,
      y: this.y + this.height * 0.75,
    };
  }

  update(_dt: number): void {
    // Vertical movement
    if (this.movingUp) {
      this.velocityY = -HELICOPTER_VERTICAL_SPEED;
    } else if (this.movingDown) {
      this.velocityY = HELICOPTER_VERTICAL_SPEED;
    } else {
      this.velocityY = 0;
    }

    // Horizontal acceleration
    if (this.accelerating) {
      this.velocityX = Math.min(this.velocityX + HELICOPTER_ACCELERATION, HELICOPTER_MAX_SPEED);
    } else if (this.decelerating) {
      this.velocityX = Math.max(this.velocityX - HELICOPTER_ACCELERATION, 0);
    } else {
      // Natural deceleration
      this.velocityX = Math.max(this.velocityX - HELICOPTER_DECELERATION, 0);
    }

    // Apply movement
    this.y += this.velocityY;

    // Keep helicopter in bounds
    const minY = 50;
    const maxY = GAME_HEIGHT - this.height - 20;
    this.y = Math.max(minY, Math.min(maxY, this.y));
  }
}
