{"file_text":"import {
  GAME_WIDTH,
  TERRAIN_BASE_HEIGHT,
  TERRAIN_AMPLITUDE,
  TERRAIN_FREQUENCY,
  SCROLL_SPEED,
} from '../constants';

export interface TerrainPoint {
  x: number;
  y: number;
}

export class Terrain {
  private points: TerrainPoint[] = [];
  private offset: number = 0;
  private segmentWidth: number = 30;

  constructor() {
    this.generateInitialTerrain();
  }

  private generateInitialTerrain(): void {
    const numPoints = Math.ceil(GAME_WIDTH / this.segmentWidth) + 5;

    for (let i = 0; i < numPoints; i++) {
      this.points.push(this.generatePoint(i * this.segmentWidth));
    }
  }

  private generatePoint(worldX: number): TerrainPoint {
    const y =
      TERRAIN_BASE_HEIGHT +
      Math.sin(worldX * TERRAIN_FREQUENCY) * TERRAIN_AMPLITUDE +
      Math.sin(worldX * TERRAIN_FREQUENCY * 2.5) * (TERRAIN_AMPLITUDE * 0.3) +
      Math.sin(worldX * TERRAIN_FREQUENCY * 0.5) * (TERRAIN_AMPLITUDE * 0.5);

    return { x: worldX - this.offset, y };
  }

  update(scrolling: boolean): void {
    if (!scrolling) return;

    this.offset += SCROLL_SPEED;

    // Update point positions
    for (const point of this.points) {
      point.x -= SCROLL_SPEED;
    }

    // Remove points that are off-screen left
    while (this.points.length > 0 && this.points[0].x < -this.segmentWidth) {
      this.points.shift();
    }

    // Add new points on the right
    while (
      this.points.length === 0 ||
      this.points[this.points.length - 1].x < GAME_WIDTH + this.segmentWidth
    ) {
      const lastX =
        this.points.length > 0
          ? this.points[this.points.length - 1].x + this.segmentWidth
          : 0;
      this.points.push(this.generatePoint(lastX + this.offset));
    }
  }

  getPoints(): TerrainPoint[] {
    return this.points;
  }

  reset(): void {
    this.offset = 0;
    this.points = [];
    this.generateInitialTerrain();
  }

  getHeightAtX(x: number): number {
    // Find the terrain height at a given x position
    for (let i = 0; i < this.points.length - 1; i++) {
      if (this.points[i].x <= x && this.points[i + 1].x >= x) {
        // Linear interpolation
        const t = (x - this.points[i].x) / (this.points[i + 1].x - this.points[i].x);
        return this.points[i].y + t * (this.points[i + 1].y - this.points[i].y);
      }
    }
    return TERRAIN_BASE_HEIGHT;
  }
}
