export function checkBulletTargetCollision(bullet: Bullet, target: Target): boolean {
  // Circle-circle collision
  const dx = bullet.x - target.getCenterX();
  const dy = bullet.y - target.getCenterY();
  const distance = Math.sqrt(dx * dx + dy * dy);
  const combinedRadius = BULLET_RADIUS + target.getHitRadius();

  return distance < combinedRadius;
}
