        if (key === 'ArrowUp') {
          this.helicopter.setMovingUp(true);
          this.gameStarted = true;
        } else if (key === 'ArrowDown') {
          this.helicopter.setMovingDown(true);
        } else if (key === 'ArrowRight') {
          this.helicopter.setAccelerating(true);
          this.gameStarted = true;
        } else if (key === 'ArrowLeft') {
          this.helicopter.setDecelerating(true);
        }
      }

      if (key === ' ') {
        this.fire();
      }
