{"file_text":"import React from 'react';
import { Position, positionsEqual } from '../types';
import './Cell.css';

interface CellProps {
  letter: string;
  position: Position;
  isSelected: boolean;
  isFound: boolean;
  foundWordIndex?: number;
  onMouseDown: (pos: Position) => void;
  onMouseEnter: (pos: Position) => void;
  onMouseUp: () => void;
}

// Color palette for found words
const FOUND_COLORS = [
  '#FFD700', // Gold
  '#FF6B6B', // Coral
  '#4ECDC4', // Teal
  '#45B7D1', // Sky blue
  '#96CEB4', // Sage
  '#FFEAA7', // Light yellow
  '#DDA0DD', // Plum
  '#98D8C8', // Mint
  '#F7DC6F', // Pale gold
  '#BB8FCE', // Light purple
];

const Cell: React.FC<CellProps> = ({
  letter,
  position,
  isSelected,
  isFound,
  foundWordIndex,
  onMouseDown,
  onMouseEnter,
  onMouseUp
}) => {
  const getBackgroundColor = () => {
    if (isFound && foundWordIndex !== undefined) {
      return FOUND_COLORS[foundWordIndex % FOUND_COLORS.length];
    }
    if (isSelected) {
      return '#87CEEB'; // Light sky blue for selection
    }
    return '#ffffff';
  };

  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    onMouseDown(position);
  };

  return (
    <div
      className={`cell ${isSelected ? 'selected' : ''} ${isFound ? 'found' : ''}`}
      style={{ backgroundColor: getBackgroundColor() }}
      onMouseDown={handleMouseDown}
      onMouseEnter={() => onMouseEnter(position)}
      onMouseUp={onMouseUp}
      data-row={position.row}
      data-col={position.col}
    >
      {letter}
    </div>
  );
};

export default Cell;

// Helper to check if a position is in the selection
export function isPositionInSelection(pos: Position, selection: Position[]): boolean {
  return selection.some(p => positionsEqual(p, pos));
}

// Helper to find the word index for a found position
export function getFoundWordIndex(
  pos: Position,
  foundWordPaths: { word: string; path: Position[] }[]
): number | undefined {
  for (let i = 0; i < foundWordPaths.length; i++) {
    if (foundWordPaths[i].path.some(p => positionsEqual(p, pos))) {
      return i;
    }
  }
  return undefined;
}
