import Grid from './components/Grid';
import FoundWords from './components/FoundWords';
import WinModal from './components/WinModal';
import Celebration from './components/Celebration';
import { useGameState } from './hooks/useGameState';
import { WORD_COUNT } from './types';
import './App.css';

function App() {
  const {
    grid,
    currentSelection,
    foundWords,
    gameOver,
    showWinModal,
    hiddenWords,
    handleCellMouseDown,
    handleCellMouseEnter,
    handleMouseUp,
    resetGame,
    dismissWinModal,
    celebrationWord,
    clearCelebration
  } = useGameState();

  // Get found word paths for highlighting
  const foundWordPaths = hiddenWords.filter(hw => foundWords.includes(hw.word));

  if (!grid) {
    return (
      <div className="App">
        <div className="loading">Loading game...</div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">🔍 SeekingTheWord</h1>
        <p className="App-subtitle">Find all {WORD_COUNT} hidden words!</p>
      </header>

      <main className="App-main">
        <Grid
          grid={grid}
          currentSelection={currentSelection}
          foundWords={foundWordPaths}
          onCellMouseDown={handleCellMouseDown}
          onCellMouseEnter={handleCellMouseEnter}
          onMouseUp={handleMouseUp}
          disabled={gameOver}
        />

        <FoundWords words={foundWords} totalWords={WORD_COUNT} />

        {gameOver && !showWinModal && (
          <button className="play-again-button" onClick={resetGame}>
            🎮 Play Again
          </button>
        )}
      </main>

      <Celebration
        isVisible={celebrationWord !== null}
        word={celebrationWord || ''}
        onComplete={clearCelebration}
      />

      <WinModal isVisible={showWinModal} onDismiss={dismissWinModal} />
    </div>
  );
}

export default App;"}

