{"file_text":"import {
  Position,
  GRID_SIZE,
  isInBounds,
  positionsEqual,
  areAdjacent,
  getWordFromPath,
  GameGrid,
  GridCell
} from '../types';

describe('Type utilities', () => {
  describe('isInBounds', () => {
    it('should return true for valid positions', () => {
      expect(isInBounds({ row: 0, col: 0 })).toBe(true);
      expect(isInBounds({ row: 5, col: 5 })).toBe(true);
      expect(isInBounds({ row: 9, col: 9 })).toBe(true);
    });

    it('should return false for out of bounds positions', () => {
      expect(isInBounds({ row: -1, col: 0 })).toBe(false);
      expect(isInBounds({ row: 0, col: -1 })).toBe(false);
      expect(isInBounds({ row: 10, col: 0 })).toBe(false);
      expect(isInBounds({ row: 0, col: 10 })).toBe(false);
      expect(isInBounds({ row: 15, col: 15 })).toBe(false);
    });

    it('should work with custom grid size', () => {
      expect(isInBounds({ row: 4, col: 4 }, 5)).toBe(true);
      expect(isInBounds({ row: 5, col: 0 }, 5)).toBe(false);
    });
  });

  describe('positionsEqual', () => {
    it('should return true for equal positions', () => {
      expect(positionsEqual({ row: 0, col: 0 }, { row: 0, col: 0 })).toBe(true);
      expect(positionsEqual({ row: 5, col: 3 }, { row: 5, col: 3 })).toBe(true);
    });

    it('should return false for different positions', () => {
      expect(positionsEqual({ row: 0, col: 0 }, { row: 0, col: 1 })).toBe(false);
      expect(positionsEqual({ row: 0, col: 0 }, { row: 1, col: 0 })).toBe(false);
      expect(positionsEqual({ row: 5, col: 3 }, { row: 3, col: 5 })).toBe(false);
    });
  });

  describe('areAdjacent', () => {
    it('should return true for horizontally adjacent cells', () => {
      expect(areAdjacent({ row: 5, col: 5 }, { row: 5, col: 6 })).toBe(true);
      expect(areAdjacent({ row: 5, col: 5 }, { row: 5, col: 4 })).toBe(true);
    });

    it('should return true for vertically adjacent cells', () => {
      expect(areAdjacent({ row: 5, col: 5 }, { row: 6, col: 5 })).toBe(true);
      expect(areAdjacent({ row: 5, col: 5 }, { row: 4, col: 5 })).toBe(true);
    });

    it('should return true for diagonally adjacent cells', () => {
      expect(areAdjacent({ row: 5, col: 5 }, { row: 6, col: 6 })).toBe(true);
      expect(areAdjacent({ row: 5, col: 5 }, { row: 4, col: 4 })).toBe(true);
      expect(areAdjacent({ row: 5, col: 5 }, { row: 6, col: 4 })).toBe(true);
      expect(areAdjacent({ row: 5, col: 5 }, { row: 4, col: 6 })).toBe(true);
    });

    it('should return false for non-adjacent cells', () => {
      expect(areAdjacent({ row: 5, col: 5 }, { row: 5, col: 7 })).toBe(false);
      expect(areAdjacent({ row: 5, col: 5 }, { row: 7, col: 5 })).toBe(false);
      expect(areAdjacent({ row: 0, col: 0 }, { row: 9, col: 9 })).toBe(false);
    });

    it('should return false for same cell', () => {
      expect(areAdjacent({ row: 5, col: 5 }, { row: 5, col: 5 })).toBe(false);
    });
  });

  describe('getWordFromPath', () => {
    it('should extract word from grid path', () => {
      const mockGrid: GameGrid = {
        size: 3,
        cells: [
          [
            { letter: 'C', position: { row: 0, col: 0 }, isPartOfWord: true },
            { letter: 'A', position: { row: 0, col: 1 }, isPartOfWord: true },
            { letter: 'T', position: { row: 0, col: 2 }, isPartOfWord: true },
          ],
          [
            { letter: 'X', position: { row: 1, col: 0 }, isPartOfWord: false },
            { letter: 'Y', position: { row: 1, col: 1 }, isPartOfWord: false },
            { letter: 'Z', position: { row: 1, col: 2 }, isPartOfWord: false },
          ],
          [
            { letter: 'A', position: { row: 2, col: 0 }, isPartOfWord: false },
            { letter: 'B', position: { row: 2, col: 1 }, isPartOfWord: false },
            { letter: 'C', position: { row: 2, col: 2 }, isPartOfWord: false },
          ],
        ]
      };

      const path: Position[] = [
        { row: 0, col: 0 },
        { row: 0, col: 1 },
        { row: 0, col: 2 }
      ];

      expect(getWordFromPath(mockGrid, path)).toBe('CAT');
    });

    it('should handle diagonal paths', () => {
      const mockGrid: GameGrid = {
        size: 3,
        cells: [
          [
            { letter: 'D', position: { row: 0, col: 0 }, isPartOfWord: true },
            { letter: 'X', position: { row: 0, col: 1 }, isPartOfWord: false },
            { letter: 'X', position: { row: 0, col: 2 }, isPartOfWord: false },
          ],
          [
            { letter: 'X', position: { row: 1, col: 0 }, isPartOfWord: false },
            { letter: 'O', position: { row: 1, col: 1 }, isPartOfWord: true },
            { letter: 'X', position: { row: 1, col: 2 }, isPartOfWord: false },
          ],
          [
            { letter: 'X', position: { row: 2, col: 0 }, isPartOfWord: false },
            { letter: 'X', position: { row: 2, col: 1 }, isPartOfWord: false },
            { letter: 'G', position: { row: 2, col: 2 }, isPartOfWord: true },
          ],
        ]
      };

      const path: Position[] = [
        { row: 0, col: 0 },
        { row: 1, col: 1 },
        { row: 2, col: 2 }
      ];

      expect(getWordFromPath(mockGrid, path)).toBe('DOG');
    });
  });
});

describe('Grid constants', () => {
  it('should have correct grid size', () => {
    expect(GRID_SIZE).toBe(10);
  });
