{"file_text":"import { generateGameGrid, checkWordMatch, doesPathMatchWord } from './gridGenerator';
import { GRID_SIZE, WORD_COUNT, PlacedWord, Position, positionsEqual } from '../types';

describe('Grid Generator', () => {
  describe('generateGameGrid', () => {
    it('should generate a grid of the correct size', () => {
      const result = generateGameGrid(5);
      expect(result).not.toBeNull();
      if (result) {
        expect(result.grid.size).toBe(GRID_SIZE);
        expect(result.grid.cells.length).toBe(GRID_SIZE);
        result.grid.cells.forEach(row => {
          expect(row.length).toBe(GRID_SIZE);
        });
      }
    });

    it('should place the requested number of words', () => {
      const result = generateGameGrid(5);
      expect(result).not.toBeNull();
      if (result) {
        expect(result.placedWords.length).toBe(5);
      }
    });

    it('should generate 10 words by default', () => {
      const result = generateGameGrid();
      expect(result).not.toBeNull();
      if (result) {
        expect(result.placedWords.length).toBe(WORD_COUNT);
      }
    });

    it('should fill all cells with letters', () => {
      const result = generateGameGrid(5);
      expect(result).not.toBeNull();
      if (result) {
        result.grid.cells.forEach(row => {
          row.forEach(cell => {
            expect(cell.letter).toMatch(/^[A-Z]$/);
          });
        });
      }
    });

    it('should not have overlapping words', () => {
      const result = generateGameGrid(10);
      expect(result).not.toBeNull();
      if (result) {
        const occupiedCells = new Set<string>();

        result.placedWords.forEach(word => {
          word.path.forEach(pos => {
            const key = `${pos.row},${pos.col}`;
            expect(occupiedCells.has(key)).toBe(false);
            occupiedCells.add(key);
          });
        });
      }
    });

    it('should place words with correct letters on the grid', () => {
      const result = generateGameGrid(5);
      expect(result).not.toBeNull();
      if (result) {
        result.placedWords.forEach(placedWord => {
          const wordFromGrid = placedWord.path
            .map(pos => result.grid.cells[pos.row][pos.col].letter)
            .join('');
          expect(wordFromGrid).toBe(placedWord.word);
        });
      }
    });

    it('should mark word cells correctly', () => {
      const result = generateGameGrid(5);
      expect(result).not.toBeNull();
      if (result) {
        result.placedWords.forEach((placedWord, wordIndex) => {
          placedWord.path.forEach(pos => {
            const cell = result.grid.cells[pos.row][pos.col];
            expect(cell.isPartOfWord).toBe(true);
          });
        });
      }
    });
  });

  describe('checkWordMatch', () => {
    const mockWords: PlacedWord[] = [
      { word: 'CAT', path: [{ row: 0, col: 0 }, { row: 0, col: 1 }, { row: 0, col: 2 }] },
      { word: 'DOG', path: [{ row: 1, col: 0 }, { row: 1, col: 1 }, { row: 1, col: 2 }] },
      { word: 'BIRD', path: [{ row: 2, col: 0 }, { row: 2, col: 1 }, { row: 2, col: 2 }, { row: 2, col: 3 }] },
    ];

    it('should find a matching word', () => {
      const result = checkWordMatch('CAT', mockWords, []);
      expect(result).not.toBeNull();
      expect(result?.word).toBe('CAT');
    });

    it('should find a word when selected backwards', () => {
      const result = checkWordMatch('TAC', mockWords, []);
      expect(result).not.toBeNull();
      expect(result?.word).toBe('CAT');
    });

    it('should not match already found words', () => {
      const result = checkWordMatch('CAT', mockWords, ['CAT']);
      expect(result).toBeNull();
    });

    it('should return null for non-matching selection', () => {
      const result = checkWordMatch('XYZ', mockWords, []);
      expect(result).toBeNull();
    });
  });

  describe('doesPathMatchWord', () => {
    const wordPath: Position[] = [
      { row: 0, col: 0 },
      { row: 0, col: 1 },
      { row: 0, col: 2 }
    ];

    it('should match identical paths', () => {
      const selectionPath = [
        { row: 0, col: 0 },
        { row: 0, col: 1 },
        { row: 0, col: 2 }
      ];
      expect(doesPathMatchWord(selectionPath, wordPath)).toBe(true);
    });

    it('should match reversed paths', () => {
      const selectionPath = [
        { row: 0, col: 2 },
        { row: 0, col: 1 },
        { row: 0, col: 0 }
      ];
      expect(doesPathMatchWord(selectionPath, wordPath)).toBe(true);
    });

    it('should not match paths of different lengths', () => {
      const selectionPath = [
        { row: 0, col: 0 },
        { row: 0, col: 1 }
      ];
      expect(doesPathMatchWord(selectionPath, wordPath)).toBe(false);
    });

    it('should not match completely different paths', () => {
      const selectionPath = [
        { row: 5, col: 5 },
        { row: 5, col: 6 },
        { row: 5, col: 7 }
      ];
      expect(doesPathMatchWord(selectionPath, wordPath)).toBe(false);
    });
  });
});
