{"file_text":"import { getRandomWords, WORD_BANK } from './wordBank';

describe('Word Bank', () => {
  describe('WORD_BANK', () => {
    it('should contain words', () => {
      expect(WORD_BANK.length).toBeGreaterThan(0);
    });

    it('should contain uppercase words', () => {
      WORD_BANK.forEach(word => {
        expect(word).toBe(word.toUpperCase());
      });
    });

    it('should contain only alphabetic characters', () => {
      WORD_BANK.forEach(word => {
        expect(word).toMatch(/^[A-Z]+$/);
      });
    });
  });

  describe('getRandomWords', () => {
    it('should return the requested number of words', () => {
      const words = getRandomWords(5);
      expect(words.length).toBe(5);
    });

    it('should return unique words', () => {
      const words = getRandomWords(10);
      const uniqueWords = new Set(words);
      expect(uniqueWords.size).toBe(words.length);
    });

    it('should respect minimum length constraint', () => {
      const words = getRandomWords(10, 5);
      words.forEach(word => {
        expect(word.length).toBeGreaterThanOrEqual(5);
      });
    });

    it('should respect maximum length constraint', () => {
      const words = getRandomWords(10, 4, 5);
      words.forEach(word => {
        expect(word.length).toBeLessThanOrEqual(5);
      });
    });

    it('should return different words on subsequent calls', () => {
      const words1 = getRandomWords(5);
      const words2 = getRandomWords(5);
      // Due to randomness, there's a tiny chance they could be the same
      // But with a large word bank, it's extremely unlikely
      const allSame = words1.every((word, i) => word === words2[i]);
      expect(allSame).toBe(false);
    });

    it('should return words within the specified length range', () => {
      const words = getRandomWords(10, 4, 6);
      words.forEach(word => {
        expect(word.length).toBeGreaterThanOrEqual(4);
        expect(word.length).toBeLessThanOrEqual(6);
      });
    });
  });
});
