23. 
24. const createInitialState = (): Omit<GameState, 'grid' | 'hiddenWords'> & { 
25.   grid: GameGrid | null; 
26.   hiddenWords: PlacedWord[] 
27. } => ({
28.   grid: null,
29.   hiddenWords: [],
30.   foundWords: [],
31.   currentSelection: [],
32.   isSelecting: false,
33.   gameOver: false,
34.   showWinModal: false
35. });
