37. export function useGameState(): UseGameStateReturn {
38.   const [state, setState] = useState<GameState & { hiddenWords: PlacedWord[] }>(() => {
39.     const result = generateGameGrid(WORD_COUNT);
40.     if (result) {
41.       return {
42.         grid: result.grid,
43.         hiddenWords: result.placedWords,
44.         foundWords: [],
45.         currentSelection: [],
46.         isSelecting: false,
47.         gameOver: false,
48.         showWinModal: false
49.       };
50.     }
51.     // Fallback - this shouldn't happen in normal circumstances
52.     return createInitialState() as GameState & { hiddenWords: PlacedWord[] };
53.   });
54. 
55.   const [foundWordPaths, setFoundWordPaths] = useState<PlacedWord[]>([]);
56.   const [celebrationWord, setCelebrationWord] = useState<string | null>(null);
57. 
58.   const resetGame = useCallback(() => {
59.     const result = generateGameGrid(WORD_COUNT);
60.     if (result) {
61.       setState({
62.         grid: result.grid,
63.         hiddenWords: result.placedWords,
64.         foundWords: [],
65.         currentSelection: [],
66.         isSelecting: false,
67.         gameOver: false,
68.         showWinModal: false
69.       });
70.       setFoundWordPaths([]);
71.       setCelebrationWord(null);
72.     }
73.   }, []);
74. 
75.   const handleCellMouseDown = useCallback((pos: Position) => {
76.     if (state.gameOver) return;
77. 
78.     setState(prev => ({
79.       ...prev,
80.       isSelecting: true,
81.       currentSelection: [pos]
82.     }));
83.   }, [state.gameOver]);
84. 
85.   const handleCellMouseEnter = useCallback((pos: Position) => {
86.     if (!state.isSelecting || state.gameOver) return;
87. 
88.     setState(prev => {
89.       const lastPos = prev.currentSelection[prev.currentSelection.length - 1];
90.       
91.       // If not adjacent to last position, don't add
92.       if (!lastPos || !areAdjacent(lastPos, pos)) {
93.         return prev;
94.       }
95. 
96.       // If already in selection (going backwards), allow it for zig-zag
97.       const existingIndex = prev.currentSelection.findIndex(p => positionsEqual(p, pos));
98.       if (existingIndex !== -1) {
99.         // Truncate selection back to this point
100.         return {
101.           ...prev,
102.           currentSelection: prev.currentSelection.slice(0, existingIndex + 1)
103.         };
104.       }
105. 
106.       return {
107.         ...prev,
108.         currentSelection: [...prev.currentSelection, pos]
109.       };
110.     });
111.   }, [state.isSelecting, state.gameOver]);
112. 
113.   const handleMouseUp = useCallback(() => {
114.     if (!state.isSelecting || state.gameOver || !state.grid) {
115.       setState(prev => ({
116.         ...prev,
117.         isSelecting: false,
118.         currentSelection: []
119.       }));
120.       return;
121.     }
122. 
123.     // Get the selected word
124.     const selectedWord = getWordFromPath(state.grid, state.currentSelection);
125.     
126.     // Check if it matches any hidden word
127.     const matchedWord = checkWordMatch(selectedWord, state.hiddenWords, state.foundWords);
128.     
129.     if (matchedWord && doesPathMatchWord(state.currentSelection, matchedWord.path)) {
130.       // Found a word!
131.       const newFoundWords = [...state.foundWords, matchedWord.word];
132.       const isGameWon = newFoundWords.length === WORD_COUNT;
133.       
134.       // Show celebration
135.       setCelebrationWord(matchedWord.word);
136.       
137.       // Add to found paths
138.       setFoundWordPaths(prev => [...prev, matchedWord]);
139.       
140.       setState(prev => ({
141.         ...prev,
142.         foundWords: newFoundWords,
143.         currentSelection: [],
144.         isSelecting: false,
145.         gameOver: isGameWon,
146.         showWinModal: isGameWon
147.       }));
148.     } else {
149.       // Invalid selection - clear it
150.       setState(prev => ({
151.         ...prev,
152.         currentSelection: [],
153.         isSelecting: false
154.       }));
155.     }
156.   }, [state.isSelecting, state.gameOver, state.grid, state.hiddenWords, state.foundWords, state.currentSelection]);
157. 
158.   const dismissWinModal = useCallback(() => {
159.     setState(prev => ({
160.       ...prev,
161.       showWinModal: false
162.     }));
163.     // Auto-restart game when modal is dismissed
164.     resetGame();
165.   }, [resetGame]);
166. 
167.   const clearCelebration = useCallback(() => {
168.     setCelebrationWord(null);
169.   }, []);
170. 
171.   // Ensure grid is generated on mount
172.   useEffect(() => {
173.     if (!state.grid) {
174.       resetGame();
175.     }
176.   }, [state.grid, resetGame]);
177. 
178.   return {
179.     ...state,
180.     grid: state.grid!,
181.     handleCellMouseDown,
182.     handleCellMouseEnter,
183.     handleMouseUp,
184.     resetGame,
185.     dismissWinModal,
186.     celebrationWord,
187.     clearCelebration,
188.     // Override found words to use paths for highlighting
189.     foundWords: state.foundWords,
190.     // Expose found word paths for the grid component
191.     hiddenWords: foundWordPaths.length > 0 ? foundWordPaths : state.hiddenWords.filter(hw => state.foundWords.includes(hw.word))
192.   };
193. }
