{"new_str":"import React from 'react';
import { Position, positionsEqual } from '../types';
import './Cell.css';

interface CellProps {
  letter: string;
  position: Position;
  isSelected: boolean;
  isFound: boolean;
  isUnfound: boolean;  // For "give up" - words user didn't find
  foundWordIndex?: number;
  unfoundWordIndex?: number;  // Index of the unfound word
  letterIndexInWord?: number; // Position of this letter in the word (0 = first)
  onMouseDown: (pos: Position) => void;
  onMouseEnter: (pos: Position) => void;
  onMouseUp: () => void;
}

// Color palette for found words (bright, celebratory)
const FOUND_COLORS = [
  '#FFD700', // Gold
  '#FF6B6B', // Coral
  '#4ECDC4', // Teal
  '#45B7D1', // Sky blue
  '#96CEB4', // Sage
  '#FFEAA7', // Light yellow
  '#DDA0DD', // Plum
  '#98D8C8', // Mint
  '#F7DC6F', // Pale gold
  '#BB8FCE', // Light purple
];

// Color palette for unfound words (darker, distinct from found)
const UNFOUND_COLORS = [
  '#8B0000', // Dark red
  '#006400', // Dark green
  '#00008B', // Dark blue
  '#8B008B', // Dark magenta
  '#FF8C00', // Dark orange
  '#2F4F4F', // Dark slate gray
  '#4B0082', // Indigo
  '#800000', // Maroon
  '#008080', // Teal dark
  '#9932CC', // Dark orchid
];

const Cell: React.FC<CellProps> = ({
  letter,
  position,
  isSelected,
  isFound,
  isUnfound,
  foundWordIndex,
  unfoundWordIndex,
  letterIndexInWord,
  onMouseDown,
  onMouseEnter,
  onMouseUp
}) => {
  const getBackgroundColor = () => {
    if (isUnfound && unfoundWordIndex !== undefined) {
      return UNFOUND_COLORS[unfoundWordIndex % UNFOUND_COLORS.length];
    }
    if (isFound && foundWordIndex !== undefined) {
      return FOUND_COLORS[foundWordIndex % FOUND_COLORS.length];
    }
    if (isSelected) {
      return '#87CEEB'; // Light sky blue for selection
    }
    return '#ffffff';
  };

  const getTextColor = () => {
    if (isUnfound) {
      return '#ffffff'; // White text for unfound words
    }
    return '#333333'; // Default dark text
  };

  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    onMouseDown(position);
  };

  // Show letter position number for unfound words
  const isFirstLetter = isUnfound && letterIndexInWord === 0;

  return (
    <div
      className={`cell ${isSelected ? 'selected' : ''} ${isFound ? 'found' : ''} ${isUnfound ? 'unfound' : ''} ${isFirstLetter ? 'first-letter' : ''}`}
      style={{ backgroundColor: getBackgroundColor(), color: getTextColor() }}
      onMouseDown={handleMouseDown}
      onMouseEnter={() => onMouseEnter(position)}
      onMouseUp={onMouseUp}
      data-row={position.row}
      data-col={position.col}
    >
      {letter}
      {isUnfound && letterIndexInWord !== undefined && (
        <span className="letter-index">{letterIndexInWord + 1}</span>
      )}
    </div>
  );
};

export default Cell;","old_str":"import React from 'react';
