}

// Helper to find the word index for a found position
export function getFoundWordIndex(
  pos: Position,
  foundWordPaths: { word: string; path: Position[] }[]
): number | undefined {
  for (let i = 0; i < foundWordPaths.length; i++) {
    if (foundWordPaths[i].path.some(p => positionsEqual(p, pos))) {
      return i;
    }
  }
  return undefined;
}

// Helper to get unfound word info for a position
export function getUnfoundWordInfo(
  pos: Position,
  allWords: { word: string; path: Position[] }[],
  foundWords: string[]
): { wordIndex: number; letterIndex: number } | undefined {
  let unfoundIndex = 0;
  for (const placedWord of allWords) {
    if (!foundWords.includes(placedWord.word)) {
      const letterIndex = placedWord.path.findIndex(p => positionsEqual(p, pos));
      if (letterIndex !== -1) {
        return { wordIndex: unfoundIndex, letterIndex };
      }
      unfoundIndex++;
    }
  }
  return undefined;
