  color: #fff;
  text-align: center;
  font-weight: 600;
}

.unfound-words-list {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center;
}

.unfound-word-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 10px 16px;
  border-radius: 8px;
  animation: fadeIn 0.3s ease forwards;
  opacity: 0;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
}

