71.         gameOver: false,
72.         showWinModal: false
73.       });
74.       setFoundWordPaths([]);
75.       setCelebrationWord(null);
76.       setGaveUp(false);
77.     }
78.   }, []);
79. 
80.   const handleCellMouseDown = useCallback((pos: Position) => {
81.     if (state.gameOver) return;
82. 
83.     setState(prev => ({
84.       ...prev,
85.       isSelecting: true,
86.       currentSelection: [pos]
