88.   }, [state.gameOver]);
89. 
90.   const handleCellMouseEnter = useCallback((pos: Position) => {
91.     if (!state.isSelecting || state.gameOver) return;
92. 
93.     setState(prev => {
94.       const lastPos = prev.currentSelection[prev.currentSelection.length - 1];
95.       
96.       // If not adjacent to last position, don't add
97.       if (!lastPos || !areAdjacent(lastPos, pos)) {
98.         return prev;
99.       }
