import { WORD_COUNT } from './types';
import './App.css';

function App() {
  const {
    grid,
    currentSelection,
    foundWords,
    gameOver,
    showWinModal,
    hiddenWords,
    allHiddenWords,
    handleCellMouseDown,
    handleCellMouseEnter,
    handleMouseUp,
    resetGame,
