    celebrationWord,
    clearCelebration,
    giveUp,
    gaveUp,
    failedAttempts
  } = useGameState();

  // Get found word paths for highlighting
  const foundWordPaths = hiddenWords.filter(hw => foundWords.includes(hw.word));

  if (!grid) {
    return (
      <div className="App">
        <div className="loading">Loading game...</div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="App-title">🔍 SeekingTheWord</h1>
      </header>

      {/* Top bar with progress and give up button */}
      <div className="top-bar">
        <div className="progress-display">
          <span className="progress-text">
