            grid={grid}
            currentSelection={currentSelection}
            foundWords={foundWordPaths}
            foundWordNames={foundWords}
            allHiddenWords={allHiddenWords}
            gaveUp={gaveUp}
            onCellMouseDown={handleCellMouseDown}
            onCellMouseEnter={handleCellMouseEnter}
            onMouseUp={handleMouseUp}
            disabled={gameOver}
          />

          {gaveUp && (
            <UnfoundWords allWords={allHiddenWords} foundWords={foundWords} />
