body {
  margin: 0;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(180deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%);
  min-height: 100vh;
}

.App {
  text-align: center;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  padding: 15px 20px;
}

.App-header {
  padding: 10px 0;
}

.App-title {
  margin: 0;
  font-size: 2.2rem;
  color: #fff;
  text-shadow: 0 0 20px rgba(102, 126, 234, 0.5);
  letter-spacing: 2px;
}

/* Top bar with progress and buttons */
.top-bar {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 30px;
  padding: 15px 20px;
  margin-bottom: 15px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

.progress-display {
  display: flex;
  align-items: center;
}

.progress-text {
  font-size: 1.3rem;
  color: #fff;
}

.progress-text strong {
  color: #4ECDC4;
  font-size: 1.5rem;
}

.top-bar-buttons {
  display: flex;
  gap: 12px;
}

/* Main 3-column layout */
.App-main {
  display: flex;
  justify-content: center;
  align-items: flex-start;
  gap: 25px;
  flex: 1;
  padding: 0 10px;
}

.sidebar {
  flex-shrink: 0;
}

.sidebar-left,
.sidebar-right {
  min-width: 180px;
  max-width: 200px;
}

.game-center {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;
}

.loading {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  font-size: 1.5rem;
  color: white;
}

/* Buttons */
.play-again-button {
  padding: 12px 28px;
  font-size: 1rem;
  font-weight: 600;
  color: white;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border: none;
  border-radius: 25px;
  cursor: pointer;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.play-again-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(102, 126, 234, 0.6);
}

.play-again-button:active {
  transform: translateY(0);
}

.give-up-button {
  padding: 12px 28px;
  font-size: 1rem;
  font-weight: 600;
  color: white;
  background: linear-gradient(135deg, #e74c3c 0%, #c0392b 100%);
  border: none;
  border-radius: 25px;
  cursor: pointer;
  box-shadow: 0 4px 15px rgba(231, 76, 60, 0.4);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.give-up-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(231, 76, 60, 0.6);
}

.give-up-button:active {
  transform: translateY(0);
}

/* Responsive adjustments */
@media (max-width: 900px) {
  .App-main {
    flex-direction: column;
    align-items: center;
  }

  .sidebar-left,
  .sidebar-right {
    width: 100%;
    max-width: 400px;
  }

  .top-bar {
    flex-direction: column;
    gap: 15px;
  }
}","old_str":"* {
