{"file_text":"/* LinkittyDo Global Styles - TreeID */

@import url('https://fonts.googleapis.com/css2?family=Luckiest+Guy&family=Nunito:wght@400;500;600;700&display=swap');

/* CSS Custom Properties */
:root {
  /* Core LinkittyDo palette */
  --ld-cream: #FDEC92;
  --ld-mint: #A9EAD2;
  --ld-ink: #161813;
  --ld-pop: #FB2B57;
  --ld-paper: #EEEDE5;

  /* Nature extension */
  --ld-forest: #2D5A3D;
  --ld-bark: #8B6914;
  --ld-leaf: #7CB342;
  --ld-autumn: #E67E22;
  --ld-winter: #95A5A6;

  /* Supporting */
  --ld-muted-green: #5E6554;
  --ld-tan: #A29A61;
  --ld-blush: #E7A790;

  /* Typography */
  --font-headline: 'Luckiest Guy', 'Bungee', cursive;
  --font-ui: 'Nunito', 'Inter', sans-serif;
  --font-mono: 'JetBrains Mono', 'Fira Code', monospace;

  --text-hero: 3rem;
  --text-title: 2rem;
  --text-heading: 1.5rem;
  --text-body: 1rem;
  --text-small: 0.875rem;
  --text-tiny: 0.75rem;

  /* Spacing */
  --space-xs: 0.25rem;
  --space-sm: 0.5rem;
  --space-md: 1rem;
  --space-lg: 1.5rem;
  --space-xl: 2rem;
  --space-2xl: 3rem;

  /* Border radius */
  --radius-sm: 4px;
  --radius-md: 8px;
  --radius-lg: 16px;
  --radius-xl: 24px;
  --radius-round: 50%;

  /* Shadows */
  --shadow-soft: 0 4px 12px rgba(22, 24, 19, 0.15);
  --shadow-hard: 4px 4px 0 var(--ld-ink);
  --shadow-hard-sm: 2px 2px 0 var(--ld-ink);
  --shadow-hard-lg: 6px 6px 0 var(--ld-ink);
  --shadow-inset: inset 2px 2px 4px rgba(22, 24, 19, 0.2);

  /* Transitions */
  --transition-fast: 0.1s ease-out;
  --transition-normal: 0.2s ease-out;
  --transition-slow: 0.4s ease-out;
}

/* Reset & Base */
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html {
  font-size: 16px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: var(--font-ui);
  font-size: var(--text-body);
  line-height: 1.6;
  color: var(--ld-ink);
  background: var(--ld-cream);
  min-height: 100vh;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
  font-family: var(--font-headline);
  line-height: 1.2;
  letter-spacing: 0.02em;
}

h1 {
  font-size: var(--text-hero);
  text-shadow: 3px 3px 0 var(--ld-mint);
}

h2 {
  font-size: var(--text-title);
}

h3 {
  font-size: var(--text-heading);
}

/* Focus states */
:focus-visible {
  outline: 3px solid var(--ld-forest);
  outline-offset: 2px;
}

/* Button base */
button {
  font-family: var(--font-ui);
  cursor: pointer;
  border: none;
  background: none;
}

/* Utility classes */
.text-center { text-align: center; }
.text-uppercase { text-transform: uppercase; }

/* App container */
#root {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}
