{"file_text":"/**
 * TreeID - Core Game Types
 */

/** Season affects which evidence is available and how trees appear */
export type Season = 'spring' | 'summer' | 'autumn' | 'winter';

/** Evidence types that can be revealed */
export type EvidenceType = 
  | 'leaf'           // Leaf shape, margin, arrangement
  | 'bark'           // Bark texture close-up
  | 'seed'           // Fruit, seed, cone, nut, samara
  | 'bud'            // Buds and twigs
  | 'silhouette'     // Overall tree shape / winter form
  | 'range'          // Geographic range map
  | 'habitat';       // Habitat photo context

/** A single piece of evidence for a tree */
export interface Evidence {
  type: EvidenceType;
  /** Description shown when revealed */
  description: string;
  /** URL or key for the evidence image/illustration */
  imageKey: string;
  /** Additional hints shown with this evidence */
  hints?: string[];
  /** Season when this evidence is most useful (null = all seasons) */
  bestSeason?: Season | null;
}

/** Difficulty rating for a species (affects point values and practice mode) */
export type Difficulty = 'easy' | 'medium' | 'hard' | 'expert';

/** Category for organizing species in practice mode */
export type TreeCategory = 
  | 'maple'
  | 'oak'
  | 'birch'
  | 'conifer'
  | 'other-deciduous';

/** A tree species in our database */
export interface TreeSpecies {
  id: string;
  commonName: string;
  scientificName: string;
  family: string;
  category: TreeCategory;
  difficulty: Difficulty;

  /** Evidence available for this species */
  evidence: Record<EvidenceType, Evidence>;

  /** IDs of species that are commonly confused with this one */
  lookalikeIds: string[];

  /** Key differentiators vs lookalikes */
  keyFeatures: string[];

  /** Fun facts or educational notes */
  funFacts: string[];

  /** Special notes (e.g., "threatened by EAB") */
  notes?: string;

  /** Regions where this tree is found */
  regions: string[];
}

/** A lookalike pair for teaching differentiation */
export interface LookalikePair {
  species1Id: string;
  species2Id: string;
  /** What makes these confusing */
  confusionReason: string;
  /** Key differences to look for */
  differentiators: string[];
}

/** Confidence level for guesses */
export type ConfidenceLevel = 50 | 75 | 90;

/** Confidence multipliers and penalties */
export const CONFIDENCE_CONFIG: Record<ConfidenceLevel, { multiplier: number; wrongPenalty: number }> = {
  50: { multiplier: 1.0, wrongPenalty: 0 },
  75: { multiplier: 1.3, wrongPenalty: 0 },
  90: { multiplier: 1.7, wrongPenalty: -80 },
};

/** A single guess attempt */
export interface Guess {
  speciesId: string;
  confidence: ConfidenceLevel;
  timestamp: number;
}

/** Result of a single tree identification */
export interface TreeResult {
  treeId: string;
  correctSpeciesId: string;
  guess: Guess | null;
  isCorrect: boolean;
  evidenceRevealed: EvidenceType[];
  timeSpentMs: number;
  score: number;
  breakdown: ScoreBreakdown;
}

/** Detailed score breakdown */
export interface ScoreBreakdown {
  basePoints: number;
  evidencePenalty: number;
  speedBonus: number;
  confidenceMultiplier: number;
  wrongPenalty: number;
  total: number;
}

export type { };
