{"file_text":"/**
 * TreeID - Game State Types
 */

import type { 
  Season, 
  EvidenceType, 
  ConfidenceLevel, 
  TreeResult,
  TreeSpecies 
} from './index';

/** Game scene/state */
export type GameScene = 
  | 'title'
  | 'how-to-play'
  | 'mode-select'
  | 'playing'
  | 'results'
  | 'herbarium'
  | 'settings';

/** Game modes */
export type GameMode = 
  | 'daily-grove'      // One tree per day
  | 'expedition'       // 10 trees, time limit
  | 'practice-lab';    // Category-specific practice

/** Current state of a playing session */
export interface PlayingState {
  mode: GameMode;
  currentTreeIndex: number;
  totalTrees: number;

  /** The current tree species being identified */
  currentTree: TreeSpecies;

  /** Which evidence has been revealed */
  revealedEvidence: EvidenceType[];

  /** Focus points remaining (for revealing evidence) */
  focusRemaining: number;

  /** Time remaining in ms (for timed modes) */
  timeRemainingMs: number;

  /** When this tree case started */
  startedAt: number;

  /** Current season (affects available evidence) */
  season: Season;

  /** Selected species ID (before confirming) */
  selectedSpeciesId: string | null;

  /** Selected confidence (before confirming) */
  selectedConfidence: ConfidenceLevel | null;

  /** Results from previous trees in this session */
  previousResults: TreeResult[];
}

/** Daily Grove specific state */
export interface DailyGroveState {
  /** Date string (YYYY-MM-DD) */
  date: string;
  /** Has player completed today's tree? */
  completed: boolean;
  /** Today's result (if completed) */
  result: TreeResult | null;
  /** Current streak count */
  streak: number;
  /** Max streak achieved */
  maxStreak: number;
}

/** Expedition mode state */
export interface ExpeditionState {
  /** Total expedition time limit in ms */
  totalTimeMs: number;
  /** Region filter */
  region: string;
  /** Season for this expedition */
  season: Season;
}

/** Practice Lab state */
export interface PracticeLabState {
  /** Category being practiced */
  category: string;
  /** Show hints during practice? */
  hintsEnabled: boolean;
}

/** Player's overall progress */
export interface PlayerProgress {
  /** Total trees correctly identified */
  totalCorrect: number;
  /** Total trees attempted */
  totalAttempted: number;
  /** Species mastered (correct 3+ times) */
  masteredSpecies: string[];
  /** Species seen at least once */
  seenSpecies: string[];
  /** High scores by mode */
  highScores: {
    expedition: number;
    dailyGrove: number;
  };
  /** Unlocked tools */
  unlockedTools: string[];
  /** Daily grove state */
  dailyGrove: DailyGroveState;
}

/** App settings */
export interface Settings {
  soundEnabled: boolean;
  musicEnabled: boolean;
  showHints: boolean;
  difficulty: 'easy' | 'normal' | 'hard';
  preferredRegion: string;
}

/** Root game state */
export interface GameState {
  scene: GameScene;
  playing: PlayingState | null;
  progress: PlayerProgress;
  settings: Settings;
}

/** Default initial state */
export const DEFAULT_SETTINGS: Settings = {
  soundEnabled: true,
  musicEnabled: true,
  showHints: true,
  difficulty: 'normal',
  preferredRegion: 'northeast-us',
};

export const DEFAULT_DAILY_GROVE: DailyGroveState = {
  date: '',
  completed: false,
  result: null,
  streak: 0,
  maxStreak: 0,
};

export const DEFAULT_PROGRESS: PlayerProgress = {
  totalCorrect: 0,
  totalAttempted: 0,
  masteredSpecies: [],
  seenSpecies: [],
  highScores: {
    expedition: 0,
    dailyGrove: 0,
  },
  unlockedTools: [],
  dailyGrove: DEFAULT_DAILY_GROVE,
};
