{"file_text":"/**
 * TreeSleuth Global Styles
 * LinkittyDo Theme Implementation
 */

/* Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Bungee&family=Nunito:wght@400;600;700;800&family=JetBrains+Mono:wght@400;600&display=swap');

/* CSS Custom Properties */
:root {
  /* Primary palette */
  --ld-cream: #FDEC92;
  --ld-mint: #A9EAD2;
  --ld-ink: #161813;
  --ld-pop: #FB2B57;
  --ld-paper: #EEEDE5;

  /* Supporting colors */
  --ld-forest: #5E6554;
  --ld-moss: #A29A61;
  --ld-bark: #8B7355;
  --ld-leaf: #4A7C59;
  --ld-peach: #E7A790;

  /* Functional */
  --ld-success: #4A7C59;
  --ld-warning: #E7A790;
  --ld-error: #FB2B57;
  --ld-info: #A9EAD2;

  /* Typography */
  --font-display: 'Bungee', 'Impact', sans-serif;
  --font-body: 'Nunito', 'Segoe UI', sans-serif;
  --font-mono: 'JetBrains Mono', 'Consolas', monospace;

  /* Shadows */
  --shadow-sm: 2px 2px 0px var(--ld-ink);
  --shadow-md: 4px 4px 0px var(--ld-ink);
  --shadow-lg: 6px 6px 0px var(--ld-ink);
  --shadow-soft: 0 4px 12px rgba(22, 24, 19, 0.15);

  /* Borders */
  --border-thin: 2px solid var(--ld-ink);
  --border-thick: 4px solid var(--ld-ink);

  /* Transitions */
  --transition-fast: 150ms ease-out;
  --transition-normal: 250ms ease-out;
  --transition-slow: 400ms ease-out;
}

/* Reset & Base */
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html {
  font-size: 16px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: var(--font-body);
  font-weight: 600;
  color: var(--ld-ink);
  background-color: var(--ld-cream);
  min-height: 100vh;
  line-height: 1.5;
}

#root {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
  font-family: var(--font-display);
  font-weight: 400;
  line-height: 1.2;
  letter-spacing: 0.02em;
}

h1 { font-size: 3rem; }
h2 { font-size: 2rem; }
h3 { font-size: 1.5rem; }
h4 { font-size: 1.25rem; }

/* Links */
a {
  color: var(--ld-pop);
  text-decoration: none;
  transition: opacity var(--transition-fast);
}

a:hover {
  opacity: 0.8;
}

/* Focus states for accessibility */
:focus-visible {
  outline: 3px solid var(--ld-pop);
  outline-offset: 2px;
}

/* Button base */
button {
  font-family: var(--font-body);
  font-weight: 700;
  cursor: pointer;
  border: none;
  background: none;
  transition: transform var(--transition-fast), box-shadow var(--transition-fast);
}

button:active:not(:disabled) {
  transform: translate(2px, 2px);
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Utility classes */
.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

.text-center { text-align: center; }
.text-pop { color: var(--ld-pop); }
.text-forest { color: var(--ld-forest); }
.bg-cream { background-color: var(--ld-cream); }
.bg-mint { background-color: var(--ld-mint); }
.bg-paper { background-color: var(--ld-paper); }
