  font-family: var(--font-display);
  font-size: clamp(2.5rem, 8vw, 4rem);
  color: var(--ld-ink);
  text-shadow: 
    4px 4px 0px var(--ld-mint),
    6px 6px 0px var(--ld-ink);
  margin-bottom: 0.5rem;
  letter-spacing: 0.05em;
}

