  font-size: 1.25rem;
  color: var(--ld-forest);
  font-style: italic;
  font-weight: 600;
}

/* Mode Selection */
.mode-selection {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 100%;
  max-width: 400px;
}

