}

.mode-card:active {
  transform: translate(2px, 2px);
  box-shadow: var(--shadow-sm);
}

.mode-icon {
  font-size: 2.5rem;
}

.mode-name {
  font-family: var(--font-display);
  font-size: 1.25rem;
